// page init
jQuery(window).load(function(){
	initAnchors();
	initTouchNav();
	initOpenClose();
	initSameHeight();
	initBackgroundResize();
	initButtonGroups();
	initRegisterToggle();
	initRollingHeader();
	initAccordionSchedule();
});

// open-close init
function initOpenClose() {
	jQuery('#nav').openClose({
		activeClass: 'active',
		opener: '.nav-opener',
		slider: '.drop',
		animSpeed: 400,
		effect: 'slide'
	});
}

// initialize smooth anchor links
function initAnchors() {
	new SmoothScroll({
		anchorLinks: '.back-to-top'
	});
}

// stretch background to fill blocks
function initBackgroundResize() {
	jQuery('.bg-stretch').each(function() {
		ImageStretcher.add({
			container: this,
			image: 'img'
		});
	});
}

// align blocks height
function initSameHeight() {
	jQuery('.news-info').sameHeight({
		elements: '.img-holder, .text-holder',
		flexible: true,
		multiLine: true
	});
	jQuery('.info-block').sameHeight({
		elements: '.img-holder, .text',
		useMinHeight: true,
		flexible: true,
		multiLine: true,
		biggestHeight: true
	});
}

// handle dropdowns on mobile devices
function initTouchNav() {
	jQuery('#nav').each(function(){
		new TouchNav({
			navBlock: this
		});
	});
}

// toggle classname states for button groups
initButtonGroups = function($) {
	jQuery('.button-group > button').on('click touchstart', function(e) {
		jQuery(this).siblings('button').removeClass('active');
		jQuery(this).addClass('active');
	});
}.bind(this,jQuery);

// Swap between login / register, keeping username and password fields
initRegisterToggle = function($) {
	var $loginElem = $('#login-register-form');
	$loginElem.find('.button-group').on('click touchstart', 'button', function(e) {
		var $target = $(e.target);
		console.log($target);
		if ($target.hasClass('register')) {
			console.log('register');
			$loginElem.find('form').attr('action','register');
			$loginElem.find('.register-fields').addClass('shown');
		} else {
			console.log('login');
			$loginElem.find('form').attr('action','login');
			$loginElem.find('.register-fields').removeClass('shown');
		}
		var submitText = ($target.hasClass('register')) ? 'Create Account' : 'Sign In';
		$loginElem.find('button[type=submit]').text(submitText);
	});
}.bind(this,jQuery);

// Hide and show the header based on scroll direction, with threshold
initRollingHeader = function($) {
	var $window = $(window),
	    last = 0,
	    threshold = 30;
	    $header = $('#header'),
	    $nav = $('#nav');
  $('#wrapper > .w1').css( 'border-top-width', $header.height() );
	var navMovement = setInterval(function() {
		if ( $nav.hasClass('active') ) return;
		if ( $window.scrollTop() - last < -threshold || $window.scrollTop() < $header.height() ) {
			$header.css('top', 0);
		} else
		if ( $window.scrollTop() - last > threshold ) {
			$header.css('top', -$header.height() - 1);
		}
		last = $window.scrollTop();
	}, 150);
}.bind(this,jQuery);


initAccordionSchedule = function($) {

	function doScheduleAccordion() {
		var $parent = $(this).parent(),
				$utility = $(this).find('.utility')[0];

		$parent.toggleClass('collapsed');
		$utility.textContent = ($parent.hasClass('collapsed')) ? 'Expand Schedule' : 'Collapse Schedule';
	}

	$('.list-schedule').on('click touchstart', '.date-heading', doScheduleAccordion)
		.slice(1);
}.bind(this,jQuery);


